package com.may.routeplansystem.config;import com.alibaba.druid.support.http.WebStatFilter;import javax.servlet.annotation.WebFilter;import javax.servlet.annotation.WebInitParam;/** * @author:dengsiyuan * @Date:2018/9/27 21:08 */@WebFilter(filterName = "druidWebStatFilter", urlPatterns = "/*",        initParams = {                @WebInitParam(name = "exclusions", value = "*.js,*.gif,*bmp,*png,*css,*.ico,/druid/*")        })public class DruidStatFilter extends WebStatFilter {}