package com.may.routeplansystem.entity.po;import com.may.routeplansystem.util.validation.insertAndUpdateGroup.Insert;import com.may.routeplansystem.util.validation.insertAndUpdateGroup.Update;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import lombok.Data;import javax.validation.constraints.NotNull;import javax.validation.constraints.Pattern;/** * @author:dengsiyuan * @Date:2018/9/25 17:03 */@Data@ApiModelpublic class VehicleMessage {    @NotNull(message = "vehicleId 不能为空", groups = Update.class)    @ApiModelProperty("车辆Id") private Integer vehicleId;    @ApiModelProperty("问题id 作备用的") private int questionId;    @NotNull(message = "type 不能为空", groups = Insert.class)    @ApiModelProperty("车辆类型") private String type;    @NotNull(message = "capacity 不能为空", groups = Insert.class)    @ApiModelProperty("车辆容量") private float capacity;    @NotNull(message = "oil 不能为空", groups = Insert.class)    @ApiModelProperty("车辆耗油量") private float oil;    @NotNull(message = "price 不能为空", groups = Insert.class)    @ApiModelProperty("车辆价格") private float price;    @Pattern(regexp = "\\d{4}-\\d{2}-\\d{2}", message = "车辆生产日期格式应为 ****-**-**", groups = Insert.class)    @ApiModelProperty("车辆生产日期") private String date;    @ApiModelProperty("车辆所属者Id") private int ownerId;}